INSERT INTO `npc_questions` VALUES ('90', 'Bonjour, je suis Edrige Valling, tireuse ? l\\\'arc renomm?e, je me ferais un plaisir de r?pondre ? vos questions sur les Arcs ou sur la fa?on de les fabriquer.', '111;109;110;209;37', '', '', '0', null);
