INSERT INTO `npc_questions` VALUES ('168', 'Depuis que la brasserie du village a ?t? d?truite par un Craqueleur assoiff?, les Brasseurs sont au ch?mage, mais le g?rant de la taverne a d?cid? de leur pr?ter la cave de la Taverne pour qu\\\'ils y installent une brasserie et puissent continuer ? produire leur d?licieuse Bi?re. \\nJ\\\'esp?re que la Brasserie sera bient?t op?rationnelle, car nos r?serves de Bi?re diminuent ? vue d\\\'oeil !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('169', 'J\\\'ai besoin de Bois de Fr?ne pour alimenter la chemin?e de la taverne, mais je n\\\'ai plus le temps d\\\'aller en chercher moi-m?me, si vous m\\\'en ramenez une unit?, je vous donnerai de l\\\'eau en ?change.', '', '', '', '', null);
