INSERT INTO `npc_questions` VALUES ('171', 'Bienvenue ? la taverne, nous pouvons vous fournir de quoi ?tancher votre soif si vous le d?sirez. \\nQu\\\'est-ce que je vous sers ?', '145;146', '', '', '0', null);
INSERT INTO `npc_questions` VALUES ('172', 'Cette Taverne est ici depuis que je suis toute petite, mon p?re m\\\'y emmenait r?guli?rement, et j\\\'ai fini par d?cider d\\\'y travailler comme serveuse.  \\nLe tavernier, qui est propri?taire de cet endroit est un homme riche et appr?ci? pour sa g?n?rosit?, n\\\'h?sitez pas ? lui parler si vous cherchez un peu de travail, il est souvent d?bord', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('173', 'Le Tavernier m\\\'a demand? de lui ramener du Houblon pour qu\\\'il le donne ? ses associ?s brasseurs, mais je n\\\'ai pas le temps d\\\'aller en acheter ? Farle Ingalsse le Paysan. \\nSi vous me ramenez une unit? de Houblon, je vous offrirai une Bi?re en ?change.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('174', 'Ssssshhhh...Passhhhh ton cheminssshhhh jeune troudboulsshhh...Seuls les ?tres les plus vilsssshhhh peuvent ?tre re?us par ma?tre Vil Smisse... \\nSssshhhhhhh....Asssshhhhhh....Uuuuuusssshhh', '', '', '', '', null);
