INSERT INTO `npc_questions` VALUES ('199', 'Hoho, un joueur ! Si vous pouvez me ramener un tr?fle ? cinq feuilles, alors je vous pr?senterai Wiss Catz, le l?gendaire Ecaflip tomb? dans la marmite de soupe de tr?fles ?tant petit.', '165', '', 'PG=6', '200', null);
