INSERT INTO `npc_questions` VALUES ('238', 'Whaaaa ! Mais que... ?  Mais qui... ? Mais... ? Ha ! Ha ! Vous ?tes perdu vous non ? Allez, ouste ! Laissez-moi passer ! j\\\'ai du travail moi m?ssieur !', '', '', '', '', null);
