INSERT INTO `npc_questions` VALUES ('258', 'Bonjour, je suis Aria Knofob. Je suis install?e depuis peu dans la province d\\\'Amakna, et pensais avoir ?t? tr?s chanceuse de trouver cette maison ? vendre pour quelques kamas.\\nIl faut dire que le cadre est agr?able, mais le bosquet qui l\\\'entoure regorge d\\\'Araknes. La lumi?re les attire, et je suis envahie. Ils se sont install?s dans le grenier.', '1005;1141', '', '', '0', null);
INSERT INTO `npc_questions` VALUES ('259', 'Oh merci ! Si vous parvenez ? me d?barrasser de ces satan?es bestioles, je saurai vous r?compenser. Hum... Perdez cet air pervers, je vous paierai en biens mat?riels !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('260', 'Oh ! merci mille fois ! Tenez, j\\\'ai trouv? ce parchemin magique au grenier lorsque j\\\'ai emm?nag?, je suis s?re qu\\\'il pourrait vous ?tre utile.', '336', '', 'RO=973,1', '', null);
INSERT INTO `npc_questions` VALUES ('261', 'D?sol? mais il reste encore des ind?sirables dans mon grenier, je les entends marcher sur le plancher. Revenez lorsque vous aurez ce que je vous ai demand', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('262', 'En tant que Cr?, vous pouvez acc?der ? l\\\'?tage, mais il est actuellement occup? par mon entraineur de dopeul.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('263', 'Cette partie du magasin est interdite aux personnes ?trang?res ? la classe des Cr?s.', '', '', '', '', null);
