INSERT INTO `npc_questions` VALUES ('1186', 'Cette tour ? Il s\\\'agit du point de rencontre des trois plus puissants Ordres de Bonta : l\\\'Ordre de L\\\'Oeil Attentif, celui de l\\\'Esprit Salvateur et celui de l\\\'ordre du Coeur Vaillant.', '', '', '', '', null);
