INSERT INTO `npc_questions` VALUES ('1446', 'Allez hop encore une racl?e ? un couard de guerrier. Qu\\\'est-ce que tu m\\\'veux toi ? Tu veux aussi ta racl?e ? Naaah je suis un peu fatigu? aujourd\\\'hui, tu n\\\'auras pas l\\\'honneur de me combattre.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1447', 'OK, c\\\'est du bon boulot, j\\\'esp?re que je n\\\'entendrais plus parler de ces crapules... \\nJe vais donner cet acte de propri?t? ? Danathor pour qu\\\'il fasse le n?cessaire.', '', '', '', '', null);
