INSERT INTO `npc_questions` VALUES ('1561', 'C\\\'est un miracle ! Mon tabi va mieux, je remercie les dieux de m\\\'?tre venu en aide !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1562', 'Ne dites surtout pas ? mes parents que je suis ici !', '', '', '', '', null);
