INSERT INTO `npc_questions` VALUES ('1565', 'Mince, je ne pensais pas que la milice viendrait fouiner dans mes affaires. Tu ne crois pas que vous devriez vous occuper de Br?kmar, plut?t que d\\\'emb?ter les gens qui exercent leur m?tier...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1566', 'Hmm, je vois que tu es intelligent, tu n\\\'as qu\\\'? dire ? Amayiro que tu as r?gl? l\\\'affaire. \\nJe te donne la totalit? des recettes de la semaine en ?change, soit 10.000 kamas...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1567', 'Tr?s bien, h?h?, n\\\'h?site pas ? venir parier sur un combat de tofu ? l\\\'occasion... \\nJe te donne aussi cette broche en guise de mon amiti', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1568', 'Comment vais-je nourrir ma femme et mes enfants si vous fermez ma boutique ? Piti? !', '', '', '', '', null);
