INSERT INTO `npc_questions` VALUES ('1869', 'Ainsi les oiseaux auraient mang? toute ma maison ? Une hypoth?se ? ?tayer, je me souviens que l\\\'un d\\\'eux m\\\'a regard? en coin de son oeil pervers l\\\'autre jour.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('1870', ' Ah AH ! Une petite fille, peut-?tre est ce une voleuse, ou pire une magicienne jalouse de mes pouvoirs ! Tout s\\\'?claire gr?ce ? ?a ! Enfin ne nous pr?cipitons pas, notons cela comme une hypoth?se probable.', '', '', '', '', null);
