INSERT INTO `npc_questions` VALUES ('1900', 'Comment faire pour qu\\\'il arr?te de penser ? ces deux idiotes...J\\\'ai bien une id?e  \\nmais il risque d\\\'?tre tr?s triste, et en col?re s\\\'il apprend que c\\\'est  \\nmoi... Ahh que c\\\'est dur *soupir*', '', '', '', '', null);
