INSERT INTO `npc_questions` VALUES ('1903', 'Bienvenue ? la boulangerie Croa, ici nous fabriquons du pain ? l\\\'ancienne ! Chaque pain fabriqu? ici est fait en respectant l\\\'ancienne tradition ! Vous le sentirez tout de suite au go?t, je vous le dit...', '1648;1647;1947', '', 'QE!10', '2117', null);
