INSERT INTO `npc_questions` VALUES ('2300', 'Ohhh... Je savais les Eniripsas susceptibles, mais quand m?me. Enfin, merci d\\\'avoir essay', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2301', 'Nib? Lulle, l\\\'Alchimiste doit pouvoir concocter une bonne infusion ? base de fleur de Dzibi. C\\\'est savoureux tu verras...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2302', 'Oh voici ma pizaille v?g?tarienne ! J\\\'ai encore oubli? mon parchemin de fid?lit?, je n\\\'aurai d?cid?ment jamais ma pizaille offerte ! \\nJ\\\'esp?re que la Lactuta sativa qu\\\'ils ont utilis?e sera plus fra?che que la derni?re fois...', '', '', '', '', null);
