INSERT INTO `npc_questions` VALUES ('2523', 'Le Grandapan demande de porter ses pri?res ? Pandawa, notre d?esse bien-aim?e ? C\\\'est une mission sacr?e... Accompagne-moi !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2524', 'Ooh PAndawAaa ! Ecoute ma pri?re : \\\" D?esse, un p\\\'tit verre, on a soif, D?esse, un p\\\'tit verre, on a soif !... \\\" Ah mais, il me manque l\\\'essentiel : une fiole de Pandneken ! Impossible de boire un coup ? la sant? de Pandawa - enfin... de lui faire une libation - sans une fiole de Pandneken ! Tu en trouveras sur le port chez un marchand ambulant ! Va vite, la d?esse s\\\'impatiente !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2525', '\\\"C\\\'est ? Boire ? boire ? boireuuuhhh, c\\\'est ? boire qu\\\'il me faut WoOooooh\\\". Que veux-tu ? Je prie. Et quand je prie, j\\\'ai soif...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2526', 'Shh, \\n*tout bas*\\navant de me parler il faut absolument que tu r?gles le probl?me de taxe, jusque l? tu n\\\'es pas le bienvenu ici. Je ne peux donc pas te parler librement, question de tradition.\\nReviens me voir apr?s on pourra discuter.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2527', 'Salut ! J\\\'ai toute sortes de boissons ferment?es ici : qu\\\'est-ce que tu veux ?', '2223;2224;2225;2226;2164', '', '', '', null);
