INSERT INTO `npc_questions` VALUES ('2599', 'Ta route a ?t? longue, disciple, mais tu es sur la bonne voie : celle qui m?ne ? la for?t mal?fique...  Avant de t\\\'y aventurer, tu devrais discuter avec notre dresseur de Dopeuls. Fais-toi la main sur l\\\'une de ses bestioles: rien de tel pour am?liorer tes techniques de combat ! Tu risques d\\\'en avoir besoin une fois l?-bas : on dit que cette for?t est oubli?e des dieux... Si tu n\\\'es pas capable de battre un Dopeul, je t\\\'interdis d\\\'envisager de fouler la terre maudite, ce serait du suicide.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2600', 'Disciple, tu sembles avoir encore beaucoup de choses ? apprendre sur les Pandawas. Que veux-tu savoir ?', '2222;2432', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2601', 'Les Pandawas sont surtout habiles avec les haches et les b?tons. Trouve un forgeur de haches ou un sculpteur de b?ton pour t\\\'?quiper, et tu seras un Pandawa combl? !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2602', 'Mmh... tu n\\\'as pas pay? de taxe ! Je ne suis pas autoris?e ? te parler !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2603', 'D\\\'apr?s l\\\'article 33, du code panda?que du Grandapan, \\\"Il est formellement interdit de vendre les boissons ? base de Bambou aux personnes non habilit?es\\\". Je suis d?sol?e !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2604', 'N\\\'as tu point lencontl? mon messager ? Tu dois te lendre ? Telldala, et pl?sente toi au chef qui se situe souvent ? l\\\'extelieul du village. Fais vite!', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2605', 'Ahah ! Ma livraison de bambou, je vais pouvoir fabriquer de nombreux boucliers gr?ce ? toi, en voil? un petit pour toi...', '', '', '', '', null);
