INSERT INTO `npc_questions` VALUES ('2636', 'Que..quoi ? Euh...connaissez-vous ma fille ?  \\nEt puis vous ?tes...euh...enfin ici a Pandala tout est possible alors... \\nAh et puis zut, d?brouillez vous avec elle, c\\\'est elle qui choisira.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2637', 'Poochan ? poochan....Ah la petite pandawette ! Mais c\\\'est une fille ! Ah c\\\'est vrai qu\\\'? Pandala tout est possible...Nakunne pourrait le prendre mal... \\nD?sol? je ne peux pas, je laisse le choix ? Nakunne, d?brouillez -vous avec elle. \\n', '', '', '', '', null);
