INSERT INTO `npc_questions` VALUES ('2678', 'Aah ! Maintenant tu es des NOTRES ! Que le bambou fleurisse sous tes pas, et fermente gr?ce ? ton haleine ! Va voir le Shaman Surivitna, il t\\\'enseignera ce que tout Pandawa doit savoir.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2679', 'Prier, c\\\'est bien. Faire des offrandes, c\\\'est mieux ! D\\\'ailleurs la d?esse fronce les sourcils en me parlant de ton cas. Ne serais-tu pas digne d\\\'?tre un de ses disciples ? Hum... Je sais !  Pour qu\\\'elle te reconnaisse comme l\\\'un de ses disciples, il faudrait que tu lui offres une de ces boissons qui la rendent euphorique... \\n', '', '', '', '', null);
