INSERT INTO `npc_questions` VALUES ('2793', 'Je vois dans tes yeux que tu es attir? par les tr?sors secrets de la for?t maudite.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2794', 'Ne dis rien ! Je sens la mort r?der pr?t de toi... tu d?sires rentrer dans l\\\'obscurit? de la for?t interdite... n\\\'est-ce pas ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2795', 'Ainsi le temps est venu pour toi d\\\'aller l? o? le temps s\\\'est arr?t? pour \\\"celle dont on doit taire le nom\\\"...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2796', 'Hmm, je ressens chez toi l\\\'envie de fouler les terres maudites du labyrinthe, serais-tu assez fou pour envisager de soigner Dark Vlad ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2797', 'Je sens en toi l\\\'envie de te mesurer ? Vlad ! Ou simplement d\\\'affronter les dangers de la for?t maudite ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2798', 'L\\\'appel de la for?t, m?me maudite, t\\\'appelle me semble-t-il...', '', '', '', '', null);
