INSERT INTO `npc_questions` VALUES ('2801', '... \\nEmeraude est cette couleur...  \\nRazeriane aime ? contempler sa beaut? dans la coquille si lisse du Dofus... \\n...Pars maintenant...', '', '', '', '', null);
