INSERT INTO `npc_questions` VALUES ('2862', 'Tu es d?sormais pr?t ? rejoindre nos lignes. \\nQuel Ordre souhaites tu int?grer ? \\nLes Guerriers de l\\\'Ordre du Coeur Saignant, les Soigneurs de l\\\'Ordre de l\\\'Esprit Malsain ou encore l\\\'Ordre de l\\\'Oeil Putride ? \\nAttention, ce choix est important, il ne te sera pas permis d\\\'en changer par la suite !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2863', 'Tu es d?sormais pr?t ? rejoindre nos lignes. \\nQuel Ordre souhaites tu int?grer ? \\nLes Guerriers de l\\\'Ordre du Coeur Saignant, les Soigneurs de l\\\'Ordre de l\\\'Esprit Malsain ou encore l\\\'Ordre de l\\\'Oeil Putride ? \\nAttention, ce choix est important, il ne te sera pas permis d\\\'en changer par la suite !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2864', 'Salutations #1, ton aura mal?fique grossit de jour en jour, je suis fier de toi ! \\nQue puis-je faire pour toi ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2865', 'J\\\'avais envoy? des Br?kmariens saigner les dragodindes de la milice de Bonta et voler leur stock de nourriture, mais ces idiots ont ?t? apeur?s par les nombreux gardes post?s devant les enclos et comme ils ?taient sur le point de se faire prendre, ils n\\\'ont rien trouv? de plus intelligent ? faire que de br?ler les entrep?ts de nourriture pour couvrir leur fuite. Par cons?quent, les Bontariens disposent toujours de leurs dragodindes et les entrep?ts de nourriture de Br?kmar n\\\'ont pas ?t? ravitaill?s. \\n \\nTu vas aller ? Astrub et trouver Raymond Santho, un marchand en relation avec Bonta. Tu dois le gagner ? notre cause quelque soit les moyens employ?s.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('2866', 'Tu arrives ? temps pour m\\\'aider. Des miliciens ont captur? une ?me et nous devons l\\\'interroger. Comme il va ?tre difficile de la forcer ? parler, nous devons d\\\'abord lui redonner son corps. Le souci est que pour une raison inconnue, la m?thode de r?surrection classique ne fonctionne pas. Si tu parviens ? ressusciter l\\\'?me en question, tu int?greras les rangs de mes apprentis.', '', '', '', '', null);
