INSERT INTO `npc_questions` VALUES ('3066', 'J\\\'ai h?te d\\\'y ?tre, pourrais-tu aller l\\\'inviter de ma part ? \\nElle s\\\'appelle Monabella, elle r?side au ch?teau d\\\'Allister et fl?ne souvent sur les quais. Pr?viens-moi d?s qu\\\'elle est disponible.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3067', 'Elle ne veut toujours pas ? Elle veut que je bouge ? Allister ne supportera jamais que je d?laisse les chantiers de r?novation de son ch?teau pour de la bagatelle. Bon, bah, essaie avec Verumacha, elle se trouve au port du ch?teau d\\\'Allister. Pr?viens-moi d?s qu\\\'elle est disponible.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3068', 'Je n\\\'ai d?cid?ment pas de chance avec les dames. Je pr?f?re que tu invites mon ami Guedin Loursgro du coup. Pense ? me ramener un peu de bi?re ? l\\\'occasion.', '', '', '', '', null);
