INSERT INTO `npc_questions` VALUES ('3088', '*Chantonne* Vous versez de la mort aux rats dans du venin de cobra.  \\nPour adoucir le m?lange, pressez trois quartiers d\\\'orange...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3089', 'Je n\\\'ai point besoin de cela pour plaire ? mon roi. Pour le ravir, il me suffit de pointer mon beau minois. Trouve quelqu\\\'un d\\\'autre qui n\\\'a pas mon charisme f?lin.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3090', 'Cette potion m\\\'int?resse. Elle me para?t fort utile pour mon commerce. Voici les Kamas. Je sens que les affaires vont d?coller et que mes bottes vont ?tre cir?es ? la bave royale.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3092', 'Il y a un requin qui n\\\'arr?te pas de me reluquer dans le port. A chaque fois que je d?charge des tonneaux d\\\'un bateau, j\\\'ai peur de tomber dans l\\\'eau et de me faire d?vorer. Il me faudrait un bon remontant pour l\\\'oublier.', '2735;2736', '', '', '', null);
