INSERT INTO `npc_questions` VALUES ('3112', 'Chantonnant : \\nSi j\\\'avais un marteau, je planterais des clous \\nSi j\\\'avais une pelle, je planterais des patates \\nSi j\\\'avais un ?conome, j\\\'?plucherais des patates \\nLalalala', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('3113', 'Je dois surveiller les prisonniers et la prison. Peux tu apporter ces sacs de patates ? Gemf? Labelle ? Il est ? la prison du Port de Madrestam ? Faut le nourrir avant qu\\\'il ne cr?ve la dalle. Sinon, apr?s, il va trouver une galerie pour s\\\'enfuir.', '2739;2740', '', 'QE!138', '3115', null);
INSERT INTO `npc_questions` VALUES ('3114', 'Le pauvre gar?on, il ne veut pas quitter sa prison. Le roi Allister l\\\'a amnisti? pourtant. Il avait vol? tout un convoi de pommes de tewe en provenance de l\\\'?le des Wabbits.', '', '', '', '', null);
