INSERT INTO `npc_questions` VALUES ('4256', 'Et bien, votre ?p?e maudite a l\\\'air vraiment inutilisable, elle est coupante, mais sa lame est tellement mal ?quilibr?e que vous devez frapper ? c?t? une fois sur deux ! \\nJe dois pouvoir l\\\'am?liorer si vous voulez.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('4257', 'Je vais avoir besoin d\\\'un tr?s bon manche pour am?liorer ton arme. Ram?ne-moi une Racine Huzohide ainsi que ton ?p?e maudite, et je pourrais rendre ton arme enfin utilisable.', '', '', '', '', null);
