INSERT INTO `npc_questions` VALUES ('5566', 'Je suis actuellement un milicien aux ordres de Bonta mais je compte me reconvertir et ouvrir une cha?ne de restaurants ? base de menus riches en calories. Il me reste juste ? trouver des chasseurs de trooll pour la viande et des p?cheurs pour m\\\'approvisionner en moumoules-frites.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('5567', 'Alors comme ?a tu es arriv? ? temps pour emp?cher la contamination des champs, je suis fier de toi. \\nJe t\\\'?l?ve au rang d\\\'Espion. Quelque chose me dit que je ne vais pas tarder ? avoir du travail pour toi. \\nVoil? 1000 kamas pour te r?compenser de tes efforts. \\nAu fait, comment ?a s\\\'est pass? avec Fidji Merline ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('5568', 'Dommage que tu sois arriv? trop tard. Quoiqu\\\'il en soit ce n\\\'est pas ta faute. Cependant, garde pour toi ce que tu sais sur les c?r?ales modifi?es, il ne faudrait pas que ?a s\\\'?bruite et que les citoyens soient mis au courant. \\nAu fait, comment ?a s\\\'est pass? avec Fidji Merline ?', '', '', '', '', null);
