INSERT INTO `npc_questions` VALUES ('5595', 'C\\\'est g?nial ! Alors tout ce qu\\\'il reste ? apporter pour pr?parer le sort de Transg?n?se ce sont 20 Graines de pavot ? Je vais m\\\'en occuper.', '', '', '', '', null);
