INSERT INTO `npc_questions` VALUES ('5606', 'Ce n\\\'est pas que je ne veux pas t\\\'aider, mais j\\\'approche de la retraite et une petite contrepartie en ?change de l\\\'information serait la bienvenue.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('5607', 'Oh, mais c\\\'est de l\\\'or pur. Merci bien. \\nTu voulais donc rencontrer un membre de la famille Terra ? C\\\'est pas de chance pour toi. Mon vieux copain Terra Cogita a ?t? captur? par les bworks il y a plusieurs ann?es et depuis le temps ils ont d? s\\\'en servir comme ap?ritif. D?sol?, je pense pas que tu trouves quoi que ce soit d\\\'utile l?-bas.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('5610', 'Ceti po que je commence ? avoir des hollucinotions. Co y est, le vieux Terra Cogita il perd lo boule.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('5611', 'Ebe v\\\'lo que les bworks ils ont coptur? le vieux Terra Cogita et qu\\\'ils voulaient le faire r?tir mais que l\\\'vieux Terra Cogita il ovait plus que lo peau sur les os et qu\\\'il o dit aux bworks qu\\\'il follait l\\\'ongraisser ovont deul monger. Ceti po queul bworks ils l\\\'ont cr? et engraiss? pondont des onn?es. Que le vieux Terra Cogita l\\\'est po b?te dit. Il mettait les olimonts dons so borbe et les oiseaux ils venaient picorer lo nuit et qu\\\'il grossissait po le vieux Terra Cogita. Ceti trop con un bwork.', '', '', '', '', null);
