INSERT INTO `npc_questions` VALUES ('6170', 'Je vois que t\\\'es du genre pas b?te toi. Ca te dirait de rejoindre les rangs des guerriers de Br?kmar ? Si ?a te tente, je t\\\'offrirai une belle bourse remplie de kamas et t\\\'apprendrai quelques trucs de v?t?ran qui pourront te servir pendant les combats. \\nAlors, qu\\\'en dis-tu ?', '', '', '', '', null);
