INSERT INTO `npc_questions` VALUES ('6179', 'Je suis d?sol?e pour toi mais j\\\'ai re?u des consignes strictes. Interdiction formelle de recruter pour les milices de Br?kmar et Bonta. \\n*chuchotte* Je peux passer outre cette restriction lorsqu\\\'une personne souhaite rejoindre l\\\'alignement minoritaire, ne l\\\'oublie pas.', '23019', '', '', '0', null);
