INSERT INTO `npc_questions` VALUES ('6194', 'Laissez-mo????????????? chanter, laiiiiiiiiissez-moi... \\nOh, bonjour, je ne vous avais pas entendu approcher. \\nDites-donc, c\\\'est normal le furoncle au milieu de votre visage ? C\\\'est vraiment affreux, vous devriez faire quelque chose pour cacher ?a. \\nMais o? ai-je la t?te ? J\\\'ai oubli? de me pr?senter. Je suis Pandalida, la c?l?bre barde et gagnante de la premi?re saison de la Lascar Amakd?mie. J\\\'esp?re que vous n\\\'?tes pas venu jusqu\\\'ici pour me demander un autographe.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6205', 'Je suis sinc?rement d?sol?, mais vous n\\\'avez pas le statut n?cessaire pour acc?der ? un coffre de banque. \\n \\n(L\\\'acc?s aux coffres de banque est r?serv? aux joueurs dont le niveau est sup?rieur ? 10)', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6206', 'Un certain X ? Mouais, peut-?tre que je sais quelque chose, mais c\\\'est pas dans mes habitudes de dire des trucs au sujet des clients. Ils tiennent ? leur discr?tion, c\\\'est pour ?a qu\\\'ils viennent dans cet ?tablissement.', '', '', '', '', null);
