INSERT INTO `npc_questions` VALUES ('6208', 'Tu sais ma choupinette, la discr?tion est un des ?l?ments majeurs de mon m?tier. Je ne peux rien te dire ? ce sujet mais d\\\'autres personnes le peuvent.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6209', 'Si j\\\'?tais toi j\\\'y r?fl?chirais ? deux fois. De nombreux miliciens patrouillent ici et il me suffit de crier pour qu\\\'ils interviennent aussit?t. \\nSi tu as envie de sensations fortes, pourquoi ne pas payer un coup ? boire ? l\\\'une de nos charmantes h?tesses ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6210', 'Si j\\\'?tais toi j\\\'y r?fl?chirais ? deux fois. De nombreux miliciens patrouillent ici et il me suffit de crier pour qu\\\'ils interviennent aussit?t. \\nSi tu as envie de sensations fortes, pourquoi ne pas travailler ici ? Certains gaillards appr?cient les femmes ? forte personnalit? dans ton genre.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6211', 'Tu m\\\'as bien regard? ? Tu me prends pour un poteau indicateur ou quoi ? \\nMais attends... Tu trouves pas que ?a pue le Br?kmarien tout ? coup ?', '', '', '', '', null);
