INSERT INTO `npc_questions` VALUES ('6308', 'Vous ?tes tellement laide que m?me les pouvoirs de mille dieux seraient insuffisants pour acc?der ? votre requ?te.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6309', 'Excellent travail. Gr?ce ? toi et ? cet imb?cile de Xephir?s je vais devenir tr?s riche. Prends cette bourse et cette bouffe et ne me remercie pas, je vais me faire de l\\\'argent sur ton dos. Hahaha. Quant ? Xephir?s, il a int?r?t ? nous signaler des convois importants car je ne veux pas me retrouver avec des tonnes de sac ? patates sur le dos.', '', '', '', '', null);
