INSERT INTO `npc_questions` VALUES ('6326', 'J\\\'esp?re qu\\\'il y a toutes les commodit?s modernes. J\\\'en ai assez de devoir me d?guiser en poubelle pour passer inaper?u et je commence ? attirer tous les chachas du quartier.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6327', 'Bonjour, toi aussi tu aimes les b?tes ?', '5574;5575;5573;5572;631', '', '', '0', null);
INSERT INTO `npc_questions` VALUES ('6328', 'Tu as raison, les animaux sont beaucoup plus intelligents que les habitants de cette cit?. Moi j\\\'adore les chachas car ils sont mignons et surtout parce qu\\\'ils ont un poil aussi soyeux que le mien. Autrefois, je faisais une tourn?e ? travers tout Amakna pour pr?senter mon num?ro de dressage de chachas mais ils m\\\'ont ?t? confisqu? et mis en quarantaine apr?s avoir mang? le kido chatoyant de l\\\'intendant Danathor. Depuis, je travaille comme dompteur et dresseur de Crocs Glands pour la milice de Bonta, en attendant de r?unir assez de kamas pour payer la caution de mes chachas.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6329', 'Mais c\\\'est horrible ! Tu as un v?ritable coeur de pierre plus solide encore que celui d\\\'un craqueleur.', '', '', '', '', null);
