INSERT INTO `npc_questions` VALUES ('6331', 'Je dois dompter des Crocs Glands pour la milice de Bonta car j\\\'ai besoin d\\\'argent pour payer la caution de mes chachas mis en quarantaine par l\\\'intendant Danathor. Heureusement pour moi, j\\\'ai bient?t r?uni la somme n?cessaire.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6332', 'Vous croyez que l\\\'intendant m\\\'aurait menti ? Ce n\\\'est pas son genre. Je vais continuer ? travailler pour r?unir assez d\\\'argent.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6333', 'Mille mercis, ?a va ?tre bien pratique pour m\\\'aider ? payer la caution de mes chachas. Il me reste justement un exemplaire de la m?thode d\\\'?levage canin, prends-en soin.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6334', 'Merci ? toi mais je pr?f?re que tu gardes ton argent. C\\\'est un plaisir d\\\'offrir un exemplaire de la m?thode d\\\'?levage canin ? une chatte au si joli minois.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6335', 'Moi aussi je veux faire garder mes b?timents par des Crocs Glands ! Par Djaul, tu as int?r?t ? te d?p?cher sinon je te transforme en p?t? pour chienchien.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6336', 'Je vais tout de suite mettre ?a en pratique. Il me faut d?signer un volontaire pour dompter des Crocs Glands. \\nNon, pas toi ! J\\\'en assez de voir ta t?te et les Crocs Glands risquent de te d?vorer avant la premi?re le?on. Tu peux disposer.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6337', 'Avant de te pr?cipiter b?tement tu vas me rapporter l\\\'?me d\\\'un Croc Gland, je t\\\'expliquerai plus tard pourquoi si jamais tu n\\\'as pas compris.', '', '', '', '', null);
