INSERT INTO `npc_questions` VALUES ('6339', 'Quoi, que signifie... Je vois, tu te crois malin pas vrai ? Prends donc ce Croc Gland fra?chement dompt? avec toi, nous verrons si la m?thode d\\\'?levage canin est aussi efficace que le pr?tend Riyou Tchevlakouk. Je vais t\\\'indiquer o? a eu lieu l\\\'embuscade, d?p?che-toi d\\\'y aller et de revenir me faire un rapport.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6340', 'Quoi, que signifie... Je vois, tu te crois maligne pas vrai ? Prends donc ce Croc Gland fra?chement dompt? avec toi, nous verrons si la m?thode d\\\'?levage canin est aussi efficace que le pr?tend Riyou Tchevlakouk. Je vais t\\\'indiquer o? a eu lieu l\\\'embuscade, d?p?che-toi d\\\'y aller et de revenir me faire un rapport.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6341', 'Je ne travaille pas plus pour Bonta que pour qui que ce soit d\\\'autre. G?n?ralement j\\\'aide plut?t les personnes qui en ont besoin car vous voyez au fond je suis un sentimental. Par contre je sais que Norb, mon confr?re qui g?re l\\\'atelier au sud d\\\'Amakna, a re?u la visite de Bontariens r?cemment. Vous devriez lui poser la question.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6342', 'Et pour qui d\\\'autres voudrais-tu que je travaille ? Ne sommes-nous pas ? Bonta ? Tu devrais y r?fl?chir ? deux fois avant de poser des questions aussi stupides.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6343', 'Ca c\\\'est d?j? plus intelligent comme question. L\\\'incomp?tent de Latem Sa travaille pour Bonta mais je te l\\\'ai d?j? dit. Peut-?tre que d\\\'autres forgerons ont parfois des commandes pour la cit?, tu devrais te renseigner au village d\\\'Amakna.', '', '', '', '', null);
