INSERT INTO `npc_questions` VALUES ('6504', 'Je dois imm?diatement me rendre ? la Tour de Bonta pour surveiller l\\\'entr?e ? D\\\'accord, mais j\\\'esp?re avoir une prime pour ?a.', '', '', '', '', null);
