INSERT INTO `npc_questions` VALUES ('6509', 'huhuhuh?h?h? HIARHIARHIARHAHAHA !  \\nL\\\'ancien patron de la Taverne du Chabrul? tenait une salle de jeux et elle n\\\'est pas morte, contrairement ? lui.  \\nhuhuhuh?h?h? HIARHIARHIARHAHAHA !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6510', 'huhuhuh?h?h? HIARHIARHIARHAHAHA !  \\nTu es pauvre, tu n\\\'as plus un sou en poche et tu crois pouvoir survivre ? Br?kmar ? \\nhuhuhuh?h?h? HIARHIARHIARHAHAHA !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6511', 'Une salle de jeux ? Je ne vois pas de quoi vous... Ah c\\\'est vous ! Pardon, mille excuses, je ne voulais pas vous mentir. C\\\'est que... je ne peux pas en parler ? n\\\'importe qui, vous comprenez ? Tenez, je vous donne une clef pour que vous puissiez y aller m?me si je suis absente, la porte cach?e se trouve au fond de la salle. C\\\'est toujours un plaisir de recevoir un Br?kmarien de votre rang dans notre taverne. D\\\'ailleurs, ce soir je finis mon service vers 21h30 - 22h00, je vous dis ?a au cas o?...', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6512', 'Une salle de jeux ? Je ne vois pas de quoi vous... Ah c\\\'est vous ! Pardon, mille excuses, je ne voulais pas vous mentir. C\\\'est que... je ne peux pas en parler ? n\\\'importe qui, vous comprenez ? Tenez, je vous donne une clef pour que vous puissiez y aller m?me si je suis absente, la porte cach?e se trouve au fond de la salle. C\\\'est toujours un plaisir de recevoir une Br?kmarienne de votre rang dans notre taverne.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6513', 'Vous voulez dire que vous ?tes venu sp?cialement pour moi ? C\\\'est tr?s gentil, mais je dois faire des heures suppl?mentaires. Depuis la mort du patron, je n\\\'ai plus de temps pour moi. Mais ?a ne veut pas dire que ?a ne m\\\'aurait pas fait plaisir, bien au contraire. Une autre fois, c\\\'est promis.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6514', 'Hey dit dit dit dit, t\\\'as pas des kamas pour moi ? Je peux te dire tout ce que je sais sur Trape si t\\\'as des kamas pour moi. Sois pas lourd, file-moi tous tes kamas !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6515', 'Eh ! T\\\'es compl?tement barge, ?a fait mal. Tout ?a parce que tu veux pas me filer des Kamas.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6516', 'Tu dis ?a parce que je suis un Sram, c\\\'est ?a ? Pour toi les Srams c\\\'est tous les m?mes, c\\\'est ce que tu penses, pas vrai ? T\\\'as de la chance que mes 50 soient pas l? sinon tu passais ? la casserole. Tu fais partie de la milice en plus ? Alors ?a m\\\'?tonne pas que tu viennes me prendre le chou. Je sais rien sur Trape et m?me si je savais quelque chose je te dirai rien. Tu vas voir, tu vas prendre cher quand mes 50 arriveront. Bon en attendant tu me laisses tranquille d\\\'accord ? L? je fais un effort pour pas te casser en deux tu vois... non pas la peine de t\\\'?nerver, je dis juste ?a pour te pr?venir. Bon d\\\'accord je me tais, mais t\\\'as de la chance que je sache garder mon sang-froid.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6517', 'huhuhuh?h?h? HIARHIARHIARHAHAHA !  \\nT\\\'as l\\\'air d\\\'appr?cier cet endroit, mais quand je suis l? je parle pas affaires alors inutile d\\\'insister. Viens jouer avec nous ou trouve-toi une autre table. \\nhuhuhuh?h?h? HIARHIARHIARHAHAHA !', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6518', 'Salut ? toi camarade Br?kmarien. Ne dis surtout pas ? Oto que je joue pendant mon temps libre, sinon il va me punir de ne pas le faire sur mon temps de travail.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6519', 'Une Sram dis-tu ? Ouais ?a me dit quelque chose, je l\\\'ai aper?ue une fois ou deux. D\\\'ailleurs la derni?re fois que je suis venue elle a empoch? un gros paquet de Kamas et une rixe a ?clat?. Bien s?r que non je n\\\'ai pas particip? ? cette rixe, tu me prends pour qui ? Je... je n\\\'avais pas mon arme sur moi alors, euh, tu vois quoi, j\\\'ai pr?f?r? rester en retrait pour observer et m\\\'assurer que la rixe n\\\'allait pas d?g?n?rer.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6520', 'Pourquoi tu prends pas un tabouret pour jouer avec nous ? Plus on est de fous, plus on rit. Ahahaha.', '', '', '', '', null);
