INSERT INTO `npc_questions` VALUES ('6665', 'Ecoute-moi bien, ce que je fais l? doit rester entre nous. Personne d\\\'autre ne doit ?tre au courant car je risque ma place et toi ta t?te, tu as bien compris ? \\nMaintenant va voir Oto Mustam, ? la milice de Br?kmar, et dis-lui que tu viens de ma part.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6666', 'Je me disais bien que ta face de rat m\\\'?tait inconnue. Bienvenue dans la milice de Br?kmar, tu vas souffrir mais tes ennemis souffriront davantage, c\\\'est dire ? quel point ce sera douloureux pour eux. Va remercier Snori Nairb de ma part et dis-lui qu\\\'il sera pay? comme convenu. En fait non, ne le remercie pas et contente-toi de lui dire qu\\\'il a int?r?t ? m\\\'envoyer plus de monde s\\\'il compte grossir ses bourses. \\nN\\\'oublie pas de revenir ici, plusieurs t?ches t\\\'attendent et je fais pas allusion aux autres miliciens.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6667', 'Ecoute-moi bien, ce que je fais l? doit rester entre nous. Personne d\\\'autre ne doit ?tre au courant car je risque ma place et toi ta t?te, tu as bien compris ? \\nMaintenant va voir Amayiro, ? la milice de Bonta, et dis-lui que tu viens de ma part.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6668', 'Je me disais bien que ton visage ne m\\\'?tait pas familier, o? ai-je la t?te ? Tu remercieras Snori Nairb de ma part et de celle de Danathor. Reviens me voir ensuite, j\\\'ai du travail pour toi.', '', '', '', '', null);
