INSERT INTO `npc_questions` VALUES ('6759', 'Les plus dangereux restent les bworks mages des cavernes, une esp?ce rare qui est, la d?esse F?ca soit lou?e pour ?a, en voie d\\\'extinction. C\\\'est d\\\'ailleurs ? l\\\'un de ces bworks mages que l\\\'on doit la destruction de la quasi-totalit? des ormes du Monde des Douze apr?s qu\\\'il eut lanc? un envo?tement de son cr? baptis? la Grafioze.', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6760', 'Bonguour ! Gue zuis heureux de vous revoir, vous avez gangu? ba vie. Gr?ze ? vous gue ne zerai bas obligu? de guidder le villague, gue vous en zerai ?dernellebent regonnaizant. Au vaid, gu\\\'est-ze gui vous ab?ne izi ?', '', '', '', '', null);
INSERT INTO `npc_questions` VALUES ('6761', 'Guatre Zadidas zont n?zezzaires pour aggomplir le riduel et ils doivent ?dre zuffizabent egzb?ribend?. Le beilleur boyen de zavoir zi z\\\'est le gas est de z\\\'azzurer gu\\\'ils zont gabables d\\\'invoguer une Gonvlable. Le beilleur endroit ze drouve dans la bardie zud des lacs enchand?s, le nord est drop broche des ?banations de la vor?t des abragnydes zombres.', '', '', '', '', null);
